import random

LocalHost = "服务端ip地址"
import socket
import sys

from PyQt5 import QtCore, QtGui, QtWidgets, QtWebSockets


def randomMes():
    tep = str(random.randint(-20, 40))
    light = str(random.randint(0, 101))
    wet = str(random.randint(0, 101))
    return tep, light, wet


class Client(QtCore.QThread):
    client_socket = None
    result = QtCore.pyqtSignal(str)
    data = QtCore.pyqtSignal((str,str,str))

    def __init__(self):
        super(Client, self).__init__()
        self.initialize_socket()

    def initialize_socket(self):
        # 创建套接字
        self.clientsocket = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)

    def run(self):
        while True:
            self.SendMessage()
            self.sleep(10)
            self.result.emit()

    def SendMessage(self):
        self.data = randomMes()
        data_t = self.data[0]
        data_l = self.data[1]
        data_w = self.data[2]
        self.client.clientsocket.sendto(data_t.encode("utf8"), (LocalHost, 8000))
        self.client.clientsocket.sendto(data_w.encode("utf8"), (LocalHost, 8000))
        self.client.clientsocket.sendto(data_l.encode("utf8"), (LocalHost, 8000))
        self.recvfrom_server()

    def recvfrom_server(self):
        self.status, self.address = self.client.clientsocket.recvfrom(1024)
        if self.status == b'open':
            self.result.emit("Open")
        else:
            self.result.emit("Close")


